function create_delivery_obj(body){
    var obj = {};
    var user_id = body.delivery_user_ids ? body.delivery_user_ids.split(',') : "";

    for(var i=0; i < body.delivery_count; i++){
        obj[i] = {
            delivery_count      : i , 
            delivery_status     :   body.delivery_status || 0, 
            delivery_due        :   body.delivery_due || new Date.toJSON(),
            delivery_user_ids   :   user_id[i] ? user_id[i] : body.delivery_user_ids,
            delivery_via        :   body.delivery_via || "",
            delivery_task_id    :   body.delivery_task_id || "",
            delivery_task_name  :   body.delivery_task_name || "", 
            message_id          :   body.message_id || "",
            from_user_id        :   body.from_user_id || ""
        };
    }
    return obj ;
}

