                template                    : detail_obj.template,
                timeout                     : create_timeout_list_obj(detail.timeout, template_arr, users_arr, "timeout") ,
                user_calculated             : create_timeout_list_obj(detail_obj.timeout, template_arr, users_arr, "user_calculated"),
                location                    : detail_obj.location,
