function populate_grand_child(all_details, child_details){
    var grand_childs = [];
    if(!isEmpty(child_details)){
        for(var i=0; i<all_details.length; i++){
            for(var k=0; k<child_details.length; k++){
                if(parseInt(all_details[i].page_id) === parseInt(child_details[k].to_page_id)){
                    grand_childs.push(all_details[i]);
                }
            }
        }
    }
    if(grand_childs.length){
        return child_details.concat(grand_childs);
    }else{
        return child_details;
    }
}

