function populated_pages(user_tasks, db_obj){
    var pages = [];

    for(var i=0; i < user_tasks.length; i++){
        var task_obj        =  populate_taskobj(user_tasks[i].page_id, db_obj.tasks);
        if(task_obj){
            var details     =  populate_details(task_obj._id, db_obj.details),
            prepare_task    =  formulate_task(task_obj, db_obj),
            prepare_details =  formulate_details(details);  

            pages.push({
                page_id :  user_tasks[i].page_id,
                user    :  user_obj || {},
                task    :  prepare_task,
                detail  :  getpage(prepare_details)
        	});
        }
    }
    return pages;
}

function getpage(details){
    var result = get_page_array(details) ,
        detail = result.detail_array ;

    if(detail.length){
        return detail ;
    }else{
        return [] ;
    }
}

function get_page_array(detail_array){

    for(var itr = 0 ; itr < detail_array.length; itr++){
        var count = {} , current_count_active = 0, current_count_unread = 0, active = 0, unread = 0;

        if(detail_array[itr].to_page_id){
            count = get_sub_child_count(detail_array[itr].to_page_id);

            if(check_obj(count) > 0){
                current_count_active = count.active ;
                current_count_unread = count.unread ;
            }
        }else{
            current_count_active = detail_array[itr].count.active ;
            current_count_unread = detail_array[itr].count.unread ;
        }
        active = active + current_count_active ;
        unread = unread + current_count_unread ;

        if(detail_array[itr].count){
            detail_array[itr].count.active = active ;
            detail_array[itr].count.unread = unread ;
        }
    }

    return {
        detail_array :   detail_array,
    };
}

function get_sub_child_count(to_page_id){
    var count = {active:0, unread: 0}, details = db_record.details;

    for(var itr = 0; itr < details.length; itr++){
        if(to_page_id == details[itr].page_id){
            if(details[itr].count_status){
                count.active += details[itr].count_status.active ;
                count.unread += details[itr].count_status.unread ;
            }
        }
    }
    return count;
}

