function create_selector(filter){
    var obj = {};
    if("synchronized" in filter){
        if(parseInt(filter.synchronized) === 0){
            obj.synchronized = {
               "$ne" : 1
            };
            delete filter.synchronized ;
        }
    }

    if("status" in filter){
        if(parseInt(filter.status) === 0){
            obj.status = {
               "$ne" : 1
            };
            delete filter.status ;
        }
    }
    return obj ;
}

function create_filtred_query(user_id, filter_obj){
    var filter_string = "";
    if(obj_length(filter_obj) > 0){

        if(!("user_id" in filter_obj)){
            filter_string = create_query(filter_obj) + " user_id:" + user_id;
        }

        if(("user_id" in filter_obj)){
            filter_string = create_query(filter_obj);
        }

    }else{
        filter_string = "user_id:" + user_id  ;
    }
    return filter_string;
}

function check_obj_count(obj){
    var length = Object.keys(obj).length ;

    if( ("user_id" in obj) ){
        length = length - 1 ;
    }

    return length;
}

function create_query(obj){
    var length = check_obj_count(obj),
        counter = 0,
        string = "";

    for(var key in obj){
        if(counter < length){
            string += key + ":" + obj[key] + " AND ";    
        }else{
            string += key + ":" + obj[key] ;
        }
        counter++ ;
    }
    return string;    
}

function obj_length(obj){
    return Object.keys(obj).length ;
}

