function populate_content(obj){
    if(typeof obj === "string"){
        try{
           var response = JSON.parse(obj);
           return {error: false, data: response , msg: "Json parse successfully"};
        }catch(e){
            node.warn(e);
            return {error: true, data: null, msg: JSON.stringify(e) };
        }
    }else{
       return {error: true, msg: "Param content must be of `string` class.", data: null} ;
    }
}

