            msg.api_response    = {status: 200, error:false, msg: "Record fetched successfully", records: populate_login_verify_pages(page_id, db_record), bookmark: paginated_details.bookmark};

        }else if(page_id == 1){
            msg.api_status = "API_SUCCESS";
            page_id = 1 ;
            msg.api_response    = {status: 200, error:false, msg: "Record fetched successfully", records: populate_login_verify_pages(page_id, db_record), bookmark: paginated_details.bookmark };

        }else{
            if(!user_obj.security_level){
                msg.api_status = "API_SUCCESS";
