function populate_login_verify_pages(page_id, db_obj){
    var req_page = {};
    if(!isEmpty(task_obj)){
        var single_prepare_task   =  formulate_task(task_obj, db_obj),
            single_prepare_detail =  formulate_details(paginated_details.data ? paginated_details.data : paginated_details);

        req_page = {
           page_id : page_id,
           user    : {},
           task    : single_prepare_task,
           detail  : single_prepare_detail
        };
    }
    return {req_page: req_page, pages: []};
}

/*********************** login and verify page functions ends here*******************/

function populated_pages(user_tasks, db_obj){
    var pages = [] , req_page = {};

    if(!isEmpty(page_id)){
        if(!isEmpty(task_obj)){
            var single_prepare_task   =  formulate_task(task_obj, db_obj),
                single_prepare_detail =  formulate_details(paginated_details.data ? paginated_details.data : paginated_details);

            req_page = {
               page_id : page_id,
               user    : (page_id == 11 ) ? {} : user_obj ,
               task    : single_prepare_task,
               detail  : getpage(single_prepare_detail)
            };
        }
    }
    if(!isEmpty(user_tasks)){
        for(var i in user_tasks){
            if(!isEmpty(task_obj)){
            var prepare_task    =  formulate_task(task_obj, db_obj),
                prepare_details =  formulate_details(paginated_details.data ? paginated_details.data : paginated_details);

                pages.push({
                    page_id :  user_tasks[i].page_id,
                    user    :  user_obj || {},
                    task    :  prepare_task,
                    detail  :  getpage(prepare_details)
            	});
            }
        }   
    }

    return {req_page : req_page, pages: pages};
}

function getpage(details){
    var result  = populate_counts(details),
        detail  = result.detail_array;

    if(detail.length){
        return detail ;
    }else{
        return [] ;
    }
}

function populate_counts(details){
    var detailArr=[];

    if( ( page_id !== 1)  && ( page_id !== 11) ){ // no need to store user_task for login and verify page
        for(var loop in details){
            var isUserTaskExits = checkFromUserTaskList(details[loop], db_record.user_tasks); 

            if( isEmpty(isUserTaskExits) ){ //check if already user_task is not present for that user. if not then do the following steps

                populate_ancestors(details[loop].page_id, db_record.tasks, "page_id"); //calculating ancestor task with request page_id
                populate_decendents(details[loop].page_id, db_record.details); //calculating sub childs _id with current requested page_id

                msg.updated_user_task = {   // create user_task record if user_task is not present in database 
                    table           : "user_task",
                	task_id 		: details[loop].task_id[0],
                	user_id			: user_obj._id,
                	page_id         : parseInt(details[loop].page_id),
                	synchronized    : 1,
                    status          : 0, 
                	date_updated	: null,	
                	parent_id		: details[loop].task_id[0],
                	ancestors       : ancestor_conts_array, 
                	decendents      : decendent_counts_array,
                	count_status	: {
                	   active       : 1,
                	   unread       : 0,
                	},
                	count			: {
                	    active      : 1,
                	    unread      : 0,
                	},
                	count_if		: {
                	    active      : 1,
                	    unread      : 0
                	},
                	templates		: {},
                	timeout			: {},
                	createdAt       : new Date().getTime()
                };

                details[loop].count.active = 0;     //when user_task is not present for that detail
                details[loop].count.unread = 0;     // just set default values for active and unread
                detailArr.push(details[loop]);      // and push into detailArr to sent response to client.
            }else{ //when already user_task present for that user. just get_active and unread counts from cloudant view i.e counts.rows
                msg.updated_user_task = {};
                //var detail_count = populateDetailObjWithCounts(counts.rows, details[loop]);
                // details[loop].count.active = detail_count.active; 
                // details[loop].count.unread = detail_count.unread;
                details[loop].count.active = 0; 
                details[loop].count.unread = 0;
                detailArr.push(details[loop]); 
            }
        }

    }

    if(!isEmpty(msg.updated_user_task))  msg.update_my_user_task = true;
    return {detail_array: detailArr};
}


function populateDetailObjWithCounts(rows, detail_obj){                  //this function accepts cloudant view rows and particular details obj
    var obj = {active: 0, unread: 0};

    if(isEmpty(rows)) return obj;
    if(isEmpty(detail_obj)) return obj;

    for(var itr in rows){
        if(
            (rows[itr].key[1] == detail_obj.user_id) &&                  //key[1] should contains user_id in user_task count cloudant_view
            ( if_exists_in_arr(rows[itr].key[2], detail_obj.task_id) )   // for refrence check cloudant view page_count 
        ){                                                               // for e.g emit([doc.ancestors[i], doc.user_id, doc.task_id], {active: doc.count.active, unread: doc.count.unread});
            obj.active += rows[itr].value.active ;
            obj.unread += rows[itr].value.unread ;
        }
    }

    return obj;
}

function checkFromUserTaskList(obj, user_tasks){
    var res = {};
    if(isEmpty(obj)) return res;
    if(isEmpty(user_tasks)) return res;

    for(var loop in user_tasks){
        if(
            (user_tasks[loop].user_id == user_obj._id) &&
            (if_exists_in_arr(user_tasks[loop].task_id, obj.task_id)) &&
            (user_tasks[loop].page_id == obj.page_id )
        ){
            res = user_tasks[loop];
        }
    }
    return res;
}

function populate_ancestors(id, task_arr, type){
    if(type == "page_id"){
        for(var index in task_arr){
            if(task_arr[index].page_id == id){
                if( !if_exists_in_arr( task_arr[index].parent_id, ancestor_conts_array ) ){
                    ancestor_conts_array.push(task_arr[index].parent_id.toString());
                }
                populate_ancestors( task_arr[index].parent_id, db_record.tasks, "parent_id"); 
            }
        }    
    }else{
        for(var itr in task_arr){
            if(task_arr[itr]._id == id){
                if(!isEmpty(task_arr[itr].parent_id)) {
                    if( !if_exists_in_arr( task_arr[itr].parent_id, ancestor_conts_array ) ){
                        ancestor_conts_array.push(task_arr[itr].parent_id.toString());
                        populate_ancestors( task_arr[itr].parent_id, db_record.tasks, "parent_id");
                    }
                }
            }
        }    
    }
}

function populate_decendents(id, details_arr){
    var new_page_id ;
    for(var index in details_arr){
        if(id == details_arr[index].page_id){
            if(checkIsPrivate(details_arr[index])) {
                if(!if_exists_in_arr( details_arr[index]._id, decendent_counts_array)){
                    decendent_counts_array.push(details_arr[index]._id.toString());
                    new_page_id = details_arr[index].to_page_id ;
                    break;
                }
            }
        }
    }

    if( 
        !isEmpty(new_page_id) && 
        ( parseInt(new_page_id) !== 0)
    ){
        populate_decendents(new_page_id, db_record.deatils);
    }
}


function checkIsPrivate(detail){

    if(isEmpty(user_obj)){
        return false;
    }else{
        if(("private" in detail.type)){
            if(user_obj._id == detail.user_id){
                return true;
            }else{
                return false;
            }
        }else{
            if(!isEmpty(user_obj.data)){
                var obj2 = detail.type ,
                     obj1 = user_obj.data.groups;

                if(isEmpty(obj1) || isEmpty(obj2)) return true;

                return Object.keys( obj1 ).every( function( prop ) {
             		return obj2.hasOwnProperty( prop );
             	});
            }else{
                return true;
            }
        }
    }
}

function formulate_details(detail_data){
    var detailRecord = {
        "_id": 	"",
        "image":		"",
        "to_page_id": 	"",
        "user_id": 		"",
        "task_id":      "",
        "page_id":      "",
        "from_user_id": "",
        "display_if_empty": "",
        "processed": "",
        "status": "",
    	"user_incoming":{},
        "watson_incoming":{},
        "type":         {},
        "due_date": "",
        "createdAt": "",
        "from_user":    {},
        "count":{"active":0, "unread":0}
    }, detailObj= [] ;

    if(detail_data.length){
        if(!isEmpty(user_obj)){
            for(var d in detail_data){

                if( (user_obj._id === detail_data[d].user_id) || (user_obj._id == checkFromUserId(detail_data[d]) )){
                    //when user owns detail record
                    detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                }
                else{

                    if(("public" in detail_data[d].type) ){ //check if user is admin or detail is public then display

                        detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                    }else { 

                        if(("admin" in user_obj.type)){
                            if( ("public" in detail_data[d].type)) {
                                detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                            }else if(hasSameProps(user_obj.data, detail_data[d].type)){
                                detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                            }else{
                                if(detail_data[d].user_id == "fa1785333117ba7c06b9020a6d3c0079"){ //if record is created super company
                                    detailObj.push( get_obj_data(detailRecord, detail_data[d]) );    
                                }else{
                                    detail_data.splice(d, 0);
                                }
                            }
                        }else{
                            detail_data.splice(d, 0);
                        }
                    }
                }
            }
            detailObj = removeEmptyArrayElements(detailObj);         
        }else{
            //when we don't have any user info while using access_token
            for(var k in detail_data){
                if(check_isDisplay(detail_data[k].display_if_empty)){
                    detailObj.push( get_obj_data(detailRecord, detail_data[k]) );
                }else{
                    var find_any_child = populate_child_detail(db_record.details, detail_data[k].to_page_id);
                    if(find_any_child.length){
                        detailObj.push( get_obj_data(detailRecord, detail_data[k]) );
                    }else{
                        detail_data.splice(k, 0);
                    }
                }
            }
            detailObj = removeEmptyArrayElements(detailObj);         
        }
    }

    return detailObj;
}

function hasSameProps( obj1, obj2 ) {
    var isExists = false;

    if(!isEmpty(obj1)){
        obj1 = obj1.groups ;
        if(!isEmpty(obj1)){
            isExists = Object.keys( obj1 ).every( function( prop ) {
     		    return obj2.hasOwnProperty( prop );
     	    });    
        }
    }

 	return isExists;
}

function checkFromUserId(detail_obj){
    if(isEmpty(detail_obj.from_user_id)){
        return null;
    }else{
        if(Object.prototype.toString.call(detail_obj.from_user_id) === "[object Object]"){
            return detail_obj.from_user_id.user_id;
        }else{
            return detail_obj.from_user_id;
        }
    }
}

function populate_child_detail(details, to_page_id){
    var arr = [];
    for(var i=0; i<details.length; i++){
        if( details[i].page_id === to_page_id ){
            arr.push(details[i]);
        }
    }
    return arr;
}

function formulate_task(task_obj, db_data){
    var template_data       = db_data.templates ,
    task_data               = task_obj,
    templateObj             = {},
    header_template_id      = task_data.header_template_id,
    detail_template_id      = task_data.detail_template_id,
    footer_template_id      = task_data.footer_template_id;

    for(var ind=0; ind<template_data.length; ind++){
        if(header_template_id == template_data[ind]._id){
            templateObj.header = template_data[ind];
        }
        if(detail_template_id == template_data[ind]._id){
            templateObj.detail = template_data[ind];
        }
        if(footer_template_id == template_data[ind]._id){
            templateObj.footer = template_data[ind];
        }
    }
    var taskObj = {
        "task_id":		    task_data._id,
        "task_name":		task_data.task_name,
        "page_id":          task_data.page_id,
        "parent_id":        task_data.parent_id,
        "template": 		templateObj,
        "from_page_id":		task_data.from_page_id,
        "child_task_id":    task_data.child_default_task_id,
        "date_created":     task_data.date_created
    };

    return taskObj;
}

function check_isDisplay(d){

    if(d === "1"){
        return true;
    }else if(d === 1){
        return true;
    }else if(d === "true"){
        return true;
    }else if(d === true){
        return true;
    }else if(d === "false"){
        return false;
    }else{
        return false ;
    }
}

function removeEmptyArrayElements(arr) { 
   if (!isArray(arr)) {
      return arr;
   } else {
       return arr.filter( function(elem) { 
          return elem !== null ;
       } ).map(removeEmptyArrayElements);
   }
}

function isArray(obj) {
    return Object.prototype.toString.call(obj) === '[object Array]';
}

function get_obj_data(obj1, obj2){
	var obj = {};
	for (var key in obj1) {
		if(obj2.hasOwnProperty(key))
			obj[key] = obj2[key];
		else
		    obj[key] = obj1[key];
	}
	//2nd operator check implied heere...
	if(!isEmpty(obj2.operator_type) && obj2.operator_type == "1") {
	    obj.isSecondOperator = true ;
	}
	return obj;
}

function if_exists_in_arr(key, array){
    return array.indexOf(key) > -1;
}

