        	date_updated	: null,	
        	parent_id		: null,
        	count_status	: {
        	   active       : 1,
        	   unread       : 0,
        	},
        	count			: {
        	    active      : 0,
        	    unread      : 0,
        	},
        	count_if		: {
        	    active      : 1,
        	    unread      : 0
        	},
        	templates		: {},
        	timeout			: {}
        };
    }else{
        msg.updated_user_task = user_task_obj ;
    }
    //node.warn("comes in updated_user_task method");
}

