            filter_string = create_query(filter_obj) + " user_id:" + user_id;
        }

        if(("user_id" in filter_obj)){
            filter_string = create_query(filter_obj);
        }

    }else{
        filter_string = "user_id:" + user_id  ;
    }
    return filter_string;
}

function check_obj_count(obj){
    var length = Object.keys(obj).length ;

    if( ("user_id" in obj) ){
