    }

    return length;
}

function create_query(obj){
    var length = check_obj_count(obj),
        counter = 0,
        string = "";

    for(var key in obj){
        if(counter < length){
            string += key + ":" + obj[key] + " AND ";    
        }else{
            string += key + ":" + obj[key] ;
        }
        counter++ ;
    }
    return string;    
}

