function populate_details(id, arr){
    var result = [];

    if(isEmpty(id)) return result;

    if(isEmpty(arr)){
        return result;
    }else{
        for(var i=0; i<arr.length; i++){
            if(arr[i].task_id == id){
                result.push(arr[i]);
            }
        }
    }    
    return result;
}

function populate_taskobj(id, arr){
    if(isEmpty(id)) return {};

    if(isEmpty(arr)){
        return {};
    }else{
        for(var i=0; i<arr.length; i++){
            if(parseInt(arr[i].page_id) === parseInt(id)){
                return arr[i];
            }
        }
    }
}

function populate_user(access_token, db_record){
    var user = {};

    if(isEmpty(access_token)) return user ;

    if(isEmpty(db_record)){
        return user;
    }else{
        for(var i=0; i < db_record.length; i++){
            if(access_token == db_record[i].access_token){
                user.user_id       = db_record[i]._id ;
                user.firstname     = db_record[i].firstname ? db_record[i].firstname : "" ;
                user.lastname      = db_record[i].lastname ? db_record[i].lastname : ""; 
                user.email         = db_record[i].email ? db_record[i].email : "";
                user.phone         = db_record[i].phone || "" ; 
                user.virtual_phone = db_record[i].virtual_phone ? db_record[i].virtual_phone: "";
                user.type          = db_record[i].type || {} ;
                user.security_level= db_record[i].security_level ;
                user.access_token  = db_record[i].access_token;
            }
        }
    }

    return user ;
}

function format_db_records(list){
    var users = [], templates = [], details=[], task_table = [], user_task = [], sorted_detail = [];
    for(var i=0; i<list.length; i++){
        if(list[i].doc.table == "template"){
            templates.push(list[i].doc);
        }
        if(list[i].doc.table == "details"){
            details.push(list[i].doc);
        }

        if(list[i].doc.table == "task_table"){
            task_table.push(list[i].doc);
        }

        if(list[i].doc.table == "users"){
            users.push(list[i].doc);
        }

        if(list[i].doc.table == "user_task"){
            user_task.push(list[i].doc);
        }
    }

    if(!isEmpty(details)){
        sorted_detail =  details.sort(function(a,b){
            sorted_detail = sorted_detail.sort(function(a,b){
            	if(typeof a.page_id === 'number'){
            		return (a.page_id - b.page_id);	
            	}else{
            		return (a.createdAt - b.createdAt);	
            	}
            });
        });
    }else{
        sorted_detail = details ;
    }

    return {users: users, templates: templates, details: sorted_detail, tasks: task_table, user_tasks: user_task} ;
}

