function formulate_details(detail_data){
    var detailRecord = {
        "_id": 	"",
        "image":		"",
        "to_page_id": 	"",
        "user_id": 		"",
        "task_id":      "",
        "page_id":      "",
        "from_user_id": "",
        "display_if_empty": "",
    	"user_incoming":{},
        "watson_incoming":{},
        "type":         {},
        "date_created": "",
        "from_user":    {},
        "count":{"active":0, "unread":0}
    }, detailObj= [] ;

    if(detail_data.length){
        if(!isEmpty(user_obj)){
            for(var d=0; d<detail_data.length; d++){

                if(user_obj.user_id === detail_data[d].user_id){
                    //when user owns detail record
                    detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                }else if(
                    ("dri_main" in detail_data[d].type) && ("dri_main" in user_obj.type) 
                ){
                    //when user owns detail record
                    detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                }else{
                    //when user doesn't own record

                    if( check_isDisplay(detail_data[d].display_if_empty) && 
                        ("public" in detail_data[d].type) 
                    ){
                        detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                    }else{
                        detail_data.splice(d, 0);
                    }
                }
            }

            detailObj = removeEmptyArrayElements(detailObj);         
        }else{
            //when we don't have any user info while using access_token
            for(var k=0; k<detail_data.length; k++){
                if(check_isDisplay(detail_data[k].display_if_empty)){
                    detailObj.push( get_obj_data(detailRecord, detail_data[k]) );
                }else{
                    var find_any_child = populate_child_detail(db_obj.details, detail_data[k].to_page_id);
                    if(find_any_child.length){
                        detailObj.push( get_obj_data(detailRecord, detail_data[k]) );
                    }else{
                        detail_data.splice(k, 0);
                    }
                }
            }
            detailObj = removeEmptyArrayElements(detailObj);         
        }
    }

    return detailObj;
}

function populate_child_detail(details, to_page_id){
    var arr = [];
    for(var i=0; i<details.length; i++){
        if( details[i].page_id === to_page_id ){
            arr.push(details[i]);
        }
    }
    return arr;
}

function formulate_task(task_obj, db_data){
    var template_data       = db_data.templates ,
    task_data               = task_obj,
    templateObj             = {},
    header_template_id      = task_data.header_template_id,
    detail_template_id      = task_data.detail_template_id,
    footer_template_id      = task_data.footer_template_id;

    for(var ind=0; ind<template_data.length; ind++){
        if(header_template_id == template_data[ind]._id){
            templateObj.header = template_data[ind];
        }
        if(detail_template_id == template_data[ind]._id){
            templateObj.detail = template_data[ind];
        }
        if(footer_template_id == template_data[ind]._id){
            templateObj.footer = template_data[ind];
        }
    }

    var taskObj = {
        "task_id":		    task_data._id,
        "task_name":		task_data.task_name,
        "parent_id":        task_data.parent_id,
        "template": 		templateObj,
        "from_page_id":		task_data.from_page_id,
        "child_task_id":    task_data.child_default_task_id,
        "date_created":     task_data.date_created
    };

    return taskObj;
}

function populate_details(id, arr){
    var result = [];

    if(isEmpty(id)) return result;

    if(isEmpty(arr)){
        return result;
    }else{
        for(var i=0; i<arr.length; i++){
            if(if_exists_in_arr(id, arr[i].task_id)){
                result.push(arr[i]);
            }
        }
    }    

    return result;
}

function populate_taskobj(id, arr){
    if(isEmpty(id)) return {};

    if(isEmpty(arr)){
        return {};
    }else{
        for(var i=0; i<arr.length; i++){
            if(parseInt(arr[i].page_id) === parseInt(id)){
                return arr[i];
            }
        }
    }
}

function populate_user(access_token, db_record){
    var user = {};

    if(isEmpty(access_token)) return user ;

    if(isEmpty(db_record)){
        return user;
    }else{
        for(var i=0; i < db_record.length; i++){
            if(access_token == db_record[i].access_token){
                user.user_id       = db_record[i]._id ;
                user.firstname     = db_record[i].firstname ? db_record[i].firstname : "" ;
                user.lastname      = db_record[i].lastname ? db_record[i].lastname : ""; 
                user.email         = db_record[i].email ? db_record[i].email : "";
                user.phone         = db_record[i].phone || "" ; 
                user.virtual_phone = db_record[i].virtual_phone ? db_record[i].virtual_phone: "";
