        msg.location_obj    = task_obj.location_id ? populate_obj(db_records.locations, task_obj.location_id): null;
    }
}

return msg;

function populate_records(list){
    var task_arr = [], detail_arr = [], user_arr = [], templates_arr = [], location_arr = [], timeout_arr = [];

    for(var itr in list){
        var records = list[itr].value;
        if(records.table == "task_table"){
            task_arr.push(records);
        }

        if(records.table == "details"){
            detail_arr.push(records);
        }

        if(records.table == "users"){
            user_arr.push(records);
        }

        if(records.table == "templates"){
            templates_arr.push(records);
        }

        if(records.table == "location"){
            location_arr.push(records);
        }

        if(records.table == "timeout"){
            timeout_arr.push(records);
        }
    }
    var obj = {tasks: task_arr, details: detail_arr, users: user_arr, templates: templates_arr, locations: location_arr, timeouts: timeout_arr};
    return obj;
}

function populate_obj(list, id){
    var obj = {};
    if(isEmpty(list)) return obj;
    if(isEmpty(id)) return obj;

    for(var itr in list){
        if(id == list[itr]._id){
            obj = list[itr];
        }
    }

    return obj;
}

function populate_user(users, access_token){
    var obj = {};
    if(isEmpty(users)) return obj;
    if(isEmpty(access_token)) return obj;

    for(var itr in users){
        if(access_token == users[itr].access_token){
            obj = users[itr];
        }
    }

    return obj;
}

