function randomString(length) {
    var result = '',
        chars  = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";

    for (var i = length; i > 0; --i) result += chars[Math.floor(Math.random() * chars.length)];
    return result;
}

function populate_user(access_token, users){
    var obj = {};

    if(isEmpty(access_token)) return obj;
    if(isEmpty(users)) return obj;

    for(var itr in users){
        if(users[itr].access_token == access_token){
            obj = users[itr];
        }
    }
    return obj;
}

function populate_updated_users(group_id, users, assistants){
    var assis = [];
    for(var ass in assistants){
        for(var itr in users){
            if(assistants[ass].detail_id == users[itr]._id){
                delete users[itr].type.virtual_group ;
                users[itr].data = {groups: {}};
                users[itr].data.groups[group_id] = true;
                assis.push(users[itr]);
            }
        }
    }
    return assis;
}

