function populate_user_task(lists, obj){
    var user_tasks = [];

    for(var itr in lists){
        if(
            ( if_exists_in_arr(lists[itr].value.task_id, obj.task_id) )&&
            (lists[itr].value.page_id  == obj.page_id)
        ){
            lists[itr].value.synchronized = 0;
            lists[itr].value.status       = 0;
            user_tasks.push(lists[itr].value);
        }
    }
    return user_tasks;
}


function if_exists_in_arr(key, array){
    return array.indexOf(key) > -1;
}


