function populate_updated_pages(all_user_pages, req_user_page){
    node.warn(all_user_pages);
    node.warn(req_user_page);

    var udpated_pages = [];

    if(isEmpty(all_user_pages)) return udpated_pages;
    if(isEmpty(req_user_page)) return udpated_pages;

    for(var itr in all_user_pages){
        for(var each in req_user_page){
            if( 
                (all_user_pages[itr].page_id == req_user_page[each].page_id) &&
                (all_user_pages[itr].user_id == req_user_page[each].user_id)
            ){
                all_user_pages[itr].count.unread = req_user_page[each].counts.unread;
                all_user_pages[itr].synchronized = req_user_page[each].synchronized;
                all_user_pages[itr].status = req_user_page[each].status;
                udpated_pages.push(all_user_pages[itr]);
            }
        }
    }

    return udpated_pages;
}

