function create_location_obj(loc_str, loc_arr){
    var locations       = {} ;
        location_ids    = loc_str ? sanitizeString(loc_str) : null;    

        for(var i=0; i< loc_arr.length; i++){
            if(location_ids !== null){
                if(location_ids.indexOf(loc_arr[i]._id) > -1 ) {
                    locations[loc_arr[i]._id] = loc_arr[i] ;
                }    
            }
        }

    return locations ;
}

function sanitizeString(str){
    if(str || str !== undefined){
        var arr = str.split(',');
        for(var i=0; i< arr.length; i++){
            arr[i] = arr[i].trim();
        }
        return arr ;
    }else{
        return false ;
    }
}

function generate_page_id(length) {
    return Math.floor(Math.pow(10, length-1) + Math.random() * (Math.pow(10, length) - Math.pow(10, length-1) - 1));
}

