function get_task_obj_from_arr(obj, arr){
    var data = null ;
    if(arr.length && arr !== null){
        for(var i=0; i < arr.length; i++){
            if( obj.page_id === arr[i].page_id && 
                sanitize(obj.task_name) === sanitize(arr[i].task_name) &&
                obj.user_id === arr[i].user_id
            ){
                data = arr[i]._id ;
            }
        }
    }
    return data ;
}

function sanitize(data){
    if(data && typeof data === "string"){
        return data.trim().toString();
    }
}

