function create_detail_message_screen(recent_task, recent_detail, all_task){
    var task = get_task_info(recent_task, all_task);

    recent_detail.task_id           = task._id ;
    recent_detail.child_task_id     = task._id ;
    recent_detail.page_id           = recent_detail.to_page_id ;
    recent_from_page_id             = recent_detail.page_id ;
    recent_detail.to_page_id        = 0 ;    
    recent_detail.user_incoming     = {
        message     : "Message screen for " +  recent_detail.user_incoming.message
    } ;
    recent_detail.watson_incoming   = {} ;
    return recent_detail ;
}

function get_task_info(task, task_arr){
    var obj = {} ;
    if(task !== null && task !== undefined){
        if(task_arr !== null && task_arr !== undefined){
            for(var i=0; i< task_arr.length; i++){
                if(
                    task.task_name === task_arr[i].task_name &&
                    task.page_id   === task_arr[i].page_id   &&
                    task.user_id   === task_arr[i].user_id 
                ){
                    obj = task_arr[i];
                }
            }
        }else {
            return obj ;
        }
    }else{
        return obj ;
    }

    return obj ;
}

