function return_user_task_obj(user_id, task_id){
    var obj = {};
    if(user_task_arr !== null && user_task_arr !== undefined){
        for(var i=0; i< user_task_arr.length; i++){
            if(
                user_task_arr[i].user_id        === user_id &&
                user_task_arr[i].parent_id      === task_id
            ){
                    obj = user_task_arr[i];
            }
        }
    }
    return obj ;
}

function update_user_task_record(obj, update_parent){
    var user_task_obj = return_user_task_obj(obj.user_id, obj.task_id),
        create_user_task_flag = true, ancestors_array= [];

    if (check_obj(user_task_obj) > 0){
    	if (user_task_obj.user_id == obj.user_id){
    	    create_user_task_flag = false ;// if one already exists then dont create again    
    	} 

    	user_task_obj.synchronized          = obj.synch ;
        user_task_obj.date_updated          = new Date().toJSON();

        if(!user_task_obj.update_read){
            if( user_task_obj.count_status.unread  && !user_task_obj.count_if.unread ){
                user_task_obj.count.unread += 1 ;  
            }
        }

        if(!user_task_obj.update_active){
            if( user_task_obj.count_status.active && user_task_obj.count_if.active){
                user_task_obj.count.active += 1 ;  
            }
        }

        if(user_task_obj.parent_id && update_parent) update_user_task_record({user_id: user_task_obj.user_id, task_id: user_task_obj.parent_id, synch:1, count: {active:0, unread:0}}, false );

        user_task_obj.update_read   = 1 ;
        user_task_obj.update_active = 1 ;


    }

    if ( create_user_task_flag ) {

        if(taskObj.parent_id){
            var if_user_task = return_user_task_obj(obj.user_id, taskObj.parent_id);

            if(check_obj(if_user_task) > 0){
                ancestors_array = if_user_task.ancestors ;    

                if( !if_exists_in_arr(taskObj.parent_id, ancestors_array) ) {
                    ancestors_array.push(taskObj.parent_id);
                }
            }else{

                if( !if_exists_in_arr(taskObj.parent_id, ancestors_array) ) ancestors_array.push(taskObj.parent_id);
            }
        }else{
            node.warn("else_user_task");     
        }

        msg.updated_user_task = {
            table           : "user_task",
        	task_id 		: obj.task_id,
        	user_id			: obj.user_id,
        	page_id         : parseInt(page_id),
        	update_read     : 0,
        	update_active   : 0, 
        	synchronized    : 1,
            status          : 0, 
        	date_updated	: null,	
        	parent_id		: obj.task_id,
        	ancestors       : ancestors_array, 
        	count_status	: {
        	   active       : 1,
        	   unread       : 0,
        	},
        	count			: {
        	    active      : 0,
        	    unread      : 0,
        	},
        	count_if		: {
        	    active      : 1,
        	    unread      : 0
        	},
        	templates		: {},
        	timeout			: {},
        	createdAt       : new Date().getTime()
        };
    }else{
        msg.updated_user_task = user_task_obj ;
    }
}

function get_sub_child_count(to_page_id){
    var count = {active:0, unread: 0} ;

    for(var itr = 0; itr < details.length; itr++){
        if(to_page_id == details[itr].page_id){
            if(details[itr].count_status){
                count.active += details[itr].count_status.active ;
                count.unread += details[itr].count_status.unread ;
            }
        }
    }

    return count;
}

