function forumlate_data(db_obj){
    if(isEmpty(db_obj)){
        msg.payload = {status: 400, error: true, msg: "No record found.", data: null};
    }else{
        if(isEmpty(db_obj.rows)){
            msg.payload = {status: 400, error: true, msg: "No record found.", data: null};
        }else{
            var res_obj = formulate_page_data(db_obj.rows),
                user    = formulate_user(msg.user_obj);

            return {
                page_id : msg.req_data.page_id,
                user    : user,
                task    : res_obj.task ,
                details : res_obj.details
            };
        }
    }
}

function formulate_user(obj){
    var user = {};
    if(isEmpty(obj.rows)){
        return {};
    }else{
        for(var i=0; i<obj.rows.length; i++){
            user.user_id = obj.rows[i].value._id ;
            user.firstname = obj.rows[i].value.firstname || "" ;
            user.lastname = obj.rows[i].value.lastname || "";
            user.email = obj.rows[i].value.email || "";
            user.phone = obj.rows[i].value.phone || "";
            user.virtual_phone = obj.rows[i].value.virtual_phone || "";
            user.type = obj.rows[i].value.type || {};
        }
    }
    return user;
}

