                "parent_id":        rows[i].value.parent_id,
                "template": 		{
                    "header_template" : formulate_template(rows[i].value.header_template_id),
                    "detail_template" : formulate_template(rows[i].value.detail_template_id),
                    "footer_template" : formulate_template(rows[i].value.footer_template_id)
                },
                "from_page_id":		rows[i].value.from_page_id,
                "child_task_id":    rows[i].value.child_default_task_id,
                "date_created":     rows[i].value.date_created
            };
        }

        if(rows[i].value.table == "details"){
            details.push({
                "_id": 	            rows[i].value._id,
                "image":		    rows[i].value.image,
                "to_page_id": 	    rows[i].value.to_page_id,
                "user_id": 		    rows[i].value.user_id,
                "task_id":          rows[i].value.task_id,
                "page_id":          rows[i].value.page_id,
                "from_user_id":     rows[i].value.from_user_id,
            	"user_incoming":    rows[i].value.user_incoming,
                "watson_incoming":  rows[i].value.watson_incoming,
                "type":             rows[i].value.type,
                "date_created":     rows[i].value.date_created,
                "from_user":        rows[i].value.from_user,
                "count":            rows[i].value.count
            });
        }
    }
    return {task: task_obj, details: details};
}

