function update_user_task_record(obj, update_parent){
    var user_task_obj = return_user_task_obj(obj.user_id, obj.task_id),
        create_user_task_flag = true ;

    if (check_obj(user_task_obj) > 0){
    	if (user_task_obj.user_id == obj.user_id){
    	    create_user_task_flag = false ;// if one already exists then dont create again    
    	} 

    	user_task_obj.synchronized          = obj.synch ;
        user_task_obj.date_updated          = new Date().toJSON();

        if(!user_task_obj.update_read){
            if( user_task_obj.count_status.unread  && !user_task_obj.count_if.unread ){
                user_task_obj.count.unread += 1 ;  
            }
        }

        if(!user_task_obj.update_active){
            if( user_task_obj.count_status.active && user_task_obj.count_if.active){
                user_task_obj.count.active += 1 ;  
            }
        }

        if(user_task_obj.parent_id && update_parent) update_user_task_record({user_id: user_task_obj.user_id, task_id: user_task_obj.parent_id, synch:1, count: {active:0, unread:0}}, false );

        user_task_obj.update_read   = 1 ;
        user_task_obj.update_active = 1 ;


    }

    if ( create_user_task_flag ) {
        msg.updated_user_task = {
        	task_id 		: obj.task_id,
        	user_id			: obj.user_id,
        	page_id         : parseInt(page_id),
        	update_read     : 0,
        	update_active   : 0, 
        	synchronized    : 1,
            status          : 0, 
        	date_updated	: null,	
        	parent_id		: null,
        	count_status	: {
        	   active       : 1,
        	   unread       : 0,
        	},
        	count			: {
        	    active      : 0,
        	    unread      : 0,
        	},
        	count_if		: {
        	    active      : 1,
        	    unread      : 0
        	},
        	templates		: {},
        	timeout			: {}
        };
    }else{
        msg.updated_user_task = user_task_obj ;
    }

}

