function create_filtred_query(user_id, filter_obj){
    var filter_string = "";
    if(obj_length(filter_obj) > 0){

        if(!("user_id" in filter_obj)){
            filter_string = create_query(filter_obj) + " user_id:" + user_id;
        }

        if(!("synchronized" in filter_obj)){
            filter_string = create_query(filter_obj) + " synchronized:false";
        }

        if(!("synchronized" in filter_obj) && !("user_id" in filter_obj)){
            filter_string = create_query(filter_obj) + " synchronized:false AND user_id" + user_id  ;
        }

        if(("user_id" in filter_obj) && ("synchronized" in filter_obj)){
            filter_string = create_query(filter_obj);
        }

    }else{
        filter_string = "user_id:" + user_id + " AND synchrnozed:false"    ;
    }
    return filter_string;
}

function check_obj_count(obj){
    var length = Object.keys(obj).length ;

    if( ("user_id" in obj) ){
        length = length - 1 ;
    }

    if( ("synchronized" in obj) ){
        length = length - 1 ;
    }

    return length;
}

function create_query(obj){
    var length = check_obj_count(obj),
        counter = 0,
        string = "";

    for(var key in obj){
        if(counter <= length){
            string += key + ":" + obj[key] + " AND ";    
        }else{
            string += key + ":" + obj[key] ;
        }
        counter++ ;
    }
    return string;    
}

