function update_user_task(user_id, task_id, status, synchronized){
    var user_task = search_user_task_from_table(user_id, task_id);
    user_task.synchronized          = synchronized ;
    user_task.date_updated          = new Date().toJSON();

    if(!user_task.update_read){
        if( ( user_task.count_status.unread === true || user_task.count_status.unread === "true") && !user_task.count_if.unread ){
            user_task.count.unread += 1 ;  
        }
    }

    if(!user_task.update_active){
        if( user_task.count_status.active && user_task.count_if.active){
            user_task.count.active += 1 ;  
        }
    }

    if(user_task.parent_id) update_user_task(user_id, user_task.parent_id, false, true);

    user_task.update_read   = true ;
    user_task.update_active = true ;
    return user_task;
}

