function create_timeout_list_obj(timeout_obj, templates, users, type){
    switch(type){
        case "timeout" : {
            if(check_obj(timeout_obj.timeout_list) > 1){

                for(var list in timeout_obj.timeout_list){
                    if(parseInt(list) !== getAttributeByIndex(timeout_obj.timeout_list, 0)){
                        timeout_obj.timeout_list[list].delivery_user_ids = get_obj_from_array(timeout_obj.timeout_list[list].delivery_user_ids, users);
                        timeout_obj.timeout_list[list].message = timeout_obj.timeout_list[list].message_id ? get_template(timeout_obj.timeout_list[list].message_id, templates) : null;
                        timeout_obj.timeout_list[list].from = timeout_obj.timeout_list[list].from_id ? get_template(timeout_obj.timeout_list[list].from_id, templates) : null;
                    }
                }

                delete timeout_obj.timeout_list[getAttributeByIndex(timeout_obj.timeout_list, 0)];
                return timeout_obj ;   
            }else{
                return {} ;
            }
            break;
        }

        case "user_calculated" : {
            if(timeout_obj.timeout_list){
                for(var key in timeout_obj.timeout_list){
                    if(parseInt(key) == getAttributeByIndex(timeout_obj.timeout_list, 0)){
                        timeout_obj.timeout_list[key].delivery_user_ids = get_obj_from_array(timeout_obj.timeout_list[key].delivery_user_ids, users);
                        timeout_obj.timeout_list[key].template = {
                            message : timeout_obj.timeout_list[key].message_id ? get_template(timeout_obj.timeout_list[key].message_id, templates) : null ,
                            from    : timeout_obj.timeout_list[key].from_id ? get_template(timeout_obj.timeout_list[key].from_id, templates) : null
                        };
                    }
                }

                return timeout_obj.timeout_list[getAttributeByIndex(timeout_obj.timeout_list, 0)] ; 
            }else{
                return {} ; 
            }
            break;
        }

        default: {
            return {};
        }
    }
}

