function getpage(page_id){
    var result = get_page_array(page_id) ,
        detail = result.detail_array ;
        //update_user_task_record({user_id: userObj.user_id, task_id: taskObj.task_id, synch : 1, count: result.count}, false);

    if(detail.length){
        return detail ;
    }else{
