function get_page_array(page_id){
    var detail_array = msg.detailObj ;

    for(var itr = 0 ; itr < detail_array.length; itr++){
        var count = {} , current_count_active = 0, current_count_unread = 0, active = 0, unread = 0;
        if(detail_array[itr].to_page_id){   
            //count = user_task(userObj.user_id, taskObj.task_id, detail_array[itr].page_id).count ;
            count = get_sub_child_count(detail_array[itr].to_page_id);
            if(count){
                current_count_active = count.active ;
                current_count_unread = count.unread ;
                update_user_task_record({user_id: userObj.user_id, task_id: taskObj.task_id, synch : 1, count: count}, false);
            }
            //count = get_sub_detail_counts(userObj.user_id, taskObj.task_id, detail_array[itr].page_id);
        }else{
            var user_task_obj = user_task(detail_array[itr].user_id, detail_array[itr].task_id, detail_array[itr].page_id);
            if (check_obj(user_task_obj) > 0){
                 // do count_if check using user_task fields as do in update_user_task
                current_count_active = user_task_obj.count.active ;
                current_count_unread = user_task_obj.count.unread ;
            }else{ // otherwise use defaults
                 // do count_if check using detail fields as do in update_user_task
                current_count_active = detail_array[itr].count.active ;
                current_count_unread = detail_array[itr].count.unread ;
            }
        }
        active = active + current_count_active ;
        unread = unread + current_count_unread ;

        detail_array[itr].count.active = active ;
        detail_array[itr].count.unread = unread ;
    }


    return {
        detail_array :   detail_array
        // count        : {
        //     active   : active ,
        //     unread   : unread 
        // }
    };
}

function check_obj(obj){
    return Object.keys(obj).length ;
}

function user_task(user_id, task_id, page_id){
    var obj = {} ;
    if(user_task_arr !== null && user_task_arr !== undefined){
        for(var i=0; i < user_task_arr.length; i++){
            if(
                user_task_arr[i].task_id === task_id &&
                user_task_arr[i].user_id === user_id &&
                user_task_arr[i].page_id === page_id 
            ){
                obj = user_task_arr[i];
            }
        }
        return obj;
    }else{
        return obj;
    }
}

function return_user_task_obj(user_id, task_id){
    var obj = {};
    if(user_task_arr !== null && user_task_arr !== undefined){
        for(var i=0; i< user_task_arr.length; i++){
            if(
                user_task_arr[i].user_id    === user_id &&
                user_task_arr[i].task_id    === task_id
            ){
                    obj = user_task_arr[i];
            }
        }
    }
    return obj ;
}

function update_user_task_record(obj, update_parent){
    var user_task_obj = return_user_task_obj(obj.user_id, obj.task_id),
        create_user_task_flag = true ;

    if (check_obj(user_task_obj) > 0){
    	if (user_task_obj.user_id == obj.user_id){
    	    create_user_task_flag = false ;// if one already exists then dont create again    
    	} 

    	user_task_obj.synchronized          = obj.synch ;
        user_task_obj.date_updated          = new Date().toJSON();

        if(!user_task_obj.update_read){
            if( user_task_obj.count_status.unread  && !user_task_obj.count_if.unread ){
                user_task_obj.count.unread += 1 ;  
            }
        }

        if(!user_task_obj.update_active){
            if( user_task_obj.count_status.active && user_task_obj.count_if.active){
                user_task_obj.count.active += 1 ;  
            }
        }

        if(user_task_obj.parent_id && update_parent) update_user_task_record({user_id: user_task_obj.user_id, task_id: user_task_obj.parent_id, synch:1, count: {active:0, unread:0}}, false );

        user_task_obj.update_read   = 1 ;
        user_task_obj.update_active = 1 ;


    }

    if ( create_user_task_flag ) {
        msg.updated_user_task = {
        	task_id 		: obj.task_id,
        	user_id			: obj.user_id,
        	page_id         : parseInt(page_id),
        	update_read     : 0,
        	update_active   : 0, 
        	synchronized    : 1,
            status          : 0, 
        	date_updated	: null,	
        	parent_id		: null,
        	count_status	: {
        	   active       : 1,
        	   unread       : 0,
        	},
        	count			: {
        	    active      : 0,
        	    unread      : 0,
        	},
        	count_if		: {
        	    active      : 1,
        	    unread      : 0
        	},
        	templates		: {},
        	timeout			: {}
        };
    }else{
        msg.updated_user_task = user_task_obj ;
    }

}

function get_sub_child_count(to_page_id){
    var count = {active:0, unread: 0} ;
    for(var itr = 0; itr < details.length; itr++){
        if(to_page_id == details[itr].page_id){
           // to_page_id = details[itr].to_page_id ;
            count.active += details[itr].count.active ;
            count.unread += details[itr].count.unread ;
        }
    }
    return count;
}
