    }else{
        return 0 ;
    }
}

function get_location_obj(locations){
    if(locations === undefined || locations === null){
        return {};
    }
    var obj = {};

    if(locations !== null && locations !== undefined){
        for(var i=0; i< locations.length; i++){
            if(locations[i]){
                obj[locations[i]._id] = locations[i] ;
            }
        }
        return obj ;
    }else{
        return obj ;
    }
}

function get_template(id, template){
    if(template === undefined || template === null){
        return {};
    }

    var obj = {} ;
    if(template !== null){
        for(var i=0; i< template.length; i++){
            if(id === template[i]._id){
                obj = template[i] ;
            }
        }    
        return obj ;
    }else{
        return obj ;
    }
}

function check_number(n){
    if(typeof n === "string" && n !== ""){
        return parseInt(n);
    }else{
        return n ;
    }
}

function generate_page_id(){
    return new Date().getTime();
}

function getAttributeByIndex(obj, index){
  var i = 0;
  for (var attr in obj){
    if (index === i){
      return attr;
    }
    i++;
  }
  return null;
}

function get_obj_from_array(id, arr){
    if(arr === null || arr === undefined){
        return {};
    }

    if(id instanceof Object){
        return id ;    
    }

    var obj = {} ;
    if(arr !== null && arr.length){
        for(var i=0; i< arr.length; i++){
            if(arr[i]._id === id){
                obj[arr[i]._id] = arr[i] ;
            }
        }
        return obj ;
    }else{
        return obj ;
    }
}

function calculate_to_page_id(task_obj, details_arr){

    var page_id = false ;
    if(task_obj !== null && details_arr !== undefined){
        var task_id = task_obj._id ;
        if(details_arr.length){
            for(var i=0; i < details_arr.length; i++){
                if(details_arr[i].task_id === task_id){
                  page_id = details_arr[i].to_page_id ;
                }
            }
            if(!page_id){
              return generate_page_id(6);
            }else{
              return page_id;
            }
        }else{
            page_id = task_obj.page_id ;
        }
    }else{
        return generate_page_id(6);
    }
}


