function populated_pages(user_tasks, db_obj){
    var pages = [] , req_page = {};

    if(!isEmpty(page_id)){
        var task = populate_taskobj(page_id, db_obj.tasks);
        if(!isEmpty(task)){
            var single_detail     =  populate_details(task._id, db_obj.details),
            single_prepare_task   =  formulate_task(task, db_obj),
            single_prepare_detail =  formulate_details(single_detail);

            req_page = {
               page_id : page_id,
               user    : user_obj || {},
               task    : single_prepare_task,
               detail  : getpage(single_prepare_detail)
            };
        }
    }

    for(var i=0; i < user_tasks.length; i++){
        var task_obj        =  populate_taskobj(user_tasks[i].page_id, db_obj.tasks);
        if(!isEmpty(task_obj)){
            var details     =  populate_details(task_obj._id, db_obj.details),
            prepare_task    =  formulate_task(task_obj, db_obj),
            prepare_details =  formulate_details(details);  

            pages.push({
                page_id :  user_tasks[i].page_id,
                user    :  user_obj || {},
                task    :  prepare_task,
                detail  :  getpage(prepare_details)
        	});
        }
    }

    return {req_page : req_page, pages: pages};
}

