function populate_updated_user_task(user_task_list, users_all_task){

    if(!isEmpty(user_task_list) && !isEmpty(user_task_list)){
        var user_task = [];

        for(var i=0; i<users_all_task.length; i++){
            for(j=0; j<user_task_list.length; j++){
                if(
                    (users_all_task[i].user_id == user_task_list[j].user_id) &&
                    (users_all_task[i].task_id == user_task_list[j].task_id) &&
                    (users_all_task[i].page_id == user_task_list[j].page_id)  
                ){
                    users_all_task[i].synchronized = 1 ;
                    users_all_task[i].status       = 1 ;
                    users_all_task[i].count.unread = user_task_list[j].unread ;
