function get_page_array(detail_array){

    for(var itr = 0 ; itr < detail_array.length; itr++){
        var count = {} , current_count_active = 0, current_count_unread = 0, active = 0, unread = 0;

        if(detail_array[itr].to_page_id){
            count = get_sub_child_count(detail_array[itr].to_page_id);

            if(check_obj(count) > 0){
                current_count_active = count.active ;
                current_count_unread = count.unread ;

                if(!isEmpty(user_obj)){
                    update_user_task_record({user_id: user_obj.user_id, task_id: task_obj._id, synch : 0, count: count}, false);    
                } 
            }
        }else{
            current_count_active = detail_array[itr].count.active ;
            current_count_unread = detail_array[itr].count.unread ;
        }
        active = active + current_count_active ;
        unread = unread + current_count_unread ;

        if(detail_array[itr].count){
            detail_array[itr].count.active = active ;
            detail_array[itr].count.unread = unread ;
        }
    }

    return {
        detail_array :   detail_array,
    };
}

function update_user_task_record(obj, update_parent){

    var user_task_obj = return_user_task_obj(obj.user_id, obj.task_id),
        create_user_task_flag = true, ancestors_array= [];

    if (check_obj(user_task_obj) > 0){

    	if (user_task_obj.user_id == obj.user_id){
    	    create_user_task_flag = false ;// if one already exists then dont create again    
    	} 

    	user_task_obj.synchronized          = obj.synch ;
        user_task_obj.date_updated          = new Date().toJSON();

        if(!user_task_obj.update_read){
            if( user_task_obj.count_status.unread  && !user_task_obj.count_if.unread ){
                user_task_obj.count.unread += 1 ;  
            }
        }

        if(!user_task_obj.update_active){
            if( user_task_obj.count_status.active && user_task_obj.count_if.active){
                user_task_obj.count.active += 1 ;  
            }
        }

        if(user_task_obj.parent_id && update_parent) update_user_task_record({user_id: user_task_obj.user_id, task_id: user_task_obj.parent_id, synch:0, count: {active:0, unread:0}}, false );

    }

    if ( create_user_task_flag ) {

        if(task_obj.parent_id){
            var if_user_task = return_user_task_obj(obj.user_id, task_obj.parent_id);

            if(check_obj(if_user_task) > 0){
                ancestors_array = if_user_task.ancestors ;    

                if( !if_exists_in_arr(task_obj.parent_id, ancestors_array) ) {
                    ancestors_array.push(task_obj.parent_id);
                }
            }else{

                if( !if_exists_in_arr(task_obj.parent_id, ancestors_array) ) ancestors_array.push(task_obj.parent_id);
            }
        }

        msg.updated_user_task = {
            table           : "user_task",
        	task_id 		: obj.task_id,
        	user_id			: obj.user_id,
        	page_id         : parseInt(page_id),
        	update_read     : 0,
        	update_active   : 0, 
        	synchronized    : 1,
            status          : 0, 
        	date_updated	: null,	
        	parent_id		: obj.task_id,
        	ancestors       : ancestors_array, 
        	count_status	: {
        	   active       : 1,
        	   unread       : 0,
        	},
        	count			: {
        	    active      : 1,
        	    unread      : 0,
        	},
        	count_if		: {
        	    active      : 1,
        	    unread      : 0
        	},
        	templates		: {},
        	timeout			: {},
        	createdAt       : new Date().getTime()
        };
    }else{
        msg.updated_user_task = user_task_obj ;
    }

    msg.isUpdateUserTask = true ;
}

function return_user_task_obj(user_id, task_id){
    var obj = {}, user_task_arr = db_obj.user_tasks ;
    if(user_task_arr !== null && user_task_arr !== undefined){
        for(var i=0; i< user_task_arr.length; i++){
            if(
                user_task_arr[i].user_id        == user_id &&
                user_task_arr[i].task_id        == task_id 
            ){
                    obj = user_task_arr[i];
            }
        }
    }
    return obj ;
}


function get_sub_child_count(to_page_id){
    var count = {active:0, unread: 0}, details = db_obj.details;

    for(var itr = 0; itr < details.length; itr++){
        if(to_page_id == details[itr].page_id){
            if(details[itr].count_status){
                count.active += details[itr].count_status.active ;
                count.unread += details[itr].count_status.unread ;
            }
        }
    }
    return count;
}

function formulate_details(detail_data){
    var detailRecord = {
        "_id": 	"",
        "image":		"",
        "to_page_id": 	"",
        "user_id": 		"",
        "task_id":      "",
        "page_id":      "",
        "from_user_id": "",
        "display_if_empty": "",
    	"user_incoming":{},
        "watson_incoming":{},
        "type":         {},
        "date_created": "",
        "from_user":    {},
        "count":{"active":0, "unread":0}
    }, detailObj= [] ;

    if(detail_data.length){
        if(!isEmpty(user_obj)){
            for(var d=0; d<detail_data.length; d++){

                if(user_obj.user_id === detail_data[d].user_id){
                    //when user owns detail record
                    detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                }else if(
                    ("dri_main" in detail_data[d].type) && ("dri_main" in user_obj.type) 
                ){
                    //when user owns detail record
                    detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                }else{
                    //when user doesn't own record

                    if( check_isDisplay(detail_data[d].display_if_empty) && 
                        ("public" in detail_data[d].type) 
                    ){
                        detailObj.push( get_obj_data(detailRecord, detail_data[d]) );
                    }else{
                        detail_data.splice(d, 0);
                    }
                }
            }

            detailObj = removeEmptyArrayElements(detailObj);         
        }else{
            //when we don't have any user info while using access_token
            for(var k=0; k<detail_data.length; k++){
                if(check_isDisplay(detail_data[k].display_if_empty)){
                    detailObj.push( get_obj_data(detailRecord, detail_data[k]) );
                }else{
                    var find_any_child = populate_child_detail(db_obj.details, detail_data[k].to_page_id);
                    if(find_any_child.length){
                        detailObj.push( get_obj_data(detailRecord, detail_data[k]) );
                    }else{
                        detail_data.splice(k, 0);
                    }
                }
            }
            detailObj = removeEmptyArrayElements(detailObj);         
        }
    }

    return detailObj;
}

function populate_child_detail(details, to_page_id){
    var arr = [];
    for(var i=0; i<details.length; i++){
        if( details[i].page_id === to_page_id ){
            arr.push(details[i]);
        }
    }
    return arr;
}

function formulate_task(task_obj, db_data){
    var template_data       = db_data.templates ,
    task_data               = task_obj,
    templateObj             = {},
    header_template_id      = task_data.header_template_id,
    detail_template_id      = task_data.detail_template_id,
    footer_template_id      = task_data.footer_template_id;

    for(var ind=0; ind<template_data.length; ind++){
        if(header_template_id == template_data[ind]._id){
            templateObj.header = template_data[ind];
        }
        if(detail_template_id == template_data[ind]._id){
            templateObj.detail = template_data[ind];
        }
        if(footer_template_id == template_data[ind]._id){
            templateObj.footer = template_data[ind];
        }
    }

    var taskObj = {
        "task_id":		    task_data._id,
        "task_name":		task_data.task_name,
        "parent_id":        task_data.parent_id,
        "template": 		templateObj,
        "from_page_id":		task_data.from_page_id,
        "child_task_id":    task_data.child_default_task_id,
        "date_created":     task_data.date_created
    };

    return taskObj;
}

function populate_details(id, arr){
    var result = [];

    if(isEmpty(id)) return result;

    if(isEmpty(arr)){
        return result;
    }else{
        for(var i=0; i<arr.length; i++){
            if(if_exists_in_arr(id, arr[i].task_id)){
                result.push(arr[i]);
            }
        }
    }    

    return result;
}

function populate_taskobj(id, arr){
    if(isEmpty(id)) return {};

    if(isEmpty(arr)){
        return {};
    }else{
        for(var i=0; i<arr.length; i++){
            if(parseInt(arr[i].page_id) === parseInt(id)){
                return arr[i];
            }
        }
    }
}

function populate_user(access_token, db_record){
    var user = {};

    if(isEmpty(access_token)) return user ;

    if(isEmpty(db_record)){
        return user;
    }else{
        for(var i=0; i < db_record.length; i++){
            if(access_token == db_record[i].access_token){
                user.user_id       = db_record[i]._id ;
                user.firstname     = db_record[i].firstname ? db_record[i].firstname : "" ;
                user.lastname      = db_record[i].lastname ? db_record[i].lastname : ""; 
                user.email         = db_record[i].email ? db_record[i].email : "";
                user.phone         = db_record[i].phone || "" ; 
                user.virtual_phone = db_record[i].virtual_phone ? db_record[i].virtual_phone: "";
                user.type          = db_record[i].type || {} ;
                user.security_level= db_record[i].security_level ;
                user.access_token  = db_record[i].access_token;
            }
        }
    }

    return user ;
}

function format_db_records(list){
    var users = [], templates = [], details=[], task_table = [], user_task = [], sorted_detail = [];
    for(var i=0; i<list.length; i++){
        if(list[i].doc.table == "template"){
            templates.push(list[i].doc);
        }
        if(list[i].doc.table == "details"){
            details.push(list[i].doc);
        }

        if(list[i].doc.table == "task_table"){
            task_table.push(list[i].doc);
        }

        if(list[i].doc.table == "users"){
            users.push(list[i].doc);
        }

        if(list[i].doc.table == "user_task"){
            user_task.push(list[i].doc);
        }
    }

    if(!isEmpty(details)){
        sorted_detail =  details.sort(function(a,b){
            sorted_detail = sorted_detail.sort(function(a,b){
            	if(typeof a.page_id === 'number'){
            		return (a.page_id - b.page_id);	
            	}else{
            		return (a.createdAt - b.createdAt);	
            	}
            });
        });
    }else{
        sorted_detail = details ;
    }

    return {users: users, templates: templates, details: sorted_detail, tasks: task_table, user_tasks: user_task} ;
}

