function create_timeout_list_obj(timeout_obj, templates, users, type){
    if(timeout_obj.name === undefined || timeout_obj.name === null){
        return {};
    }

    switch(type){
        case "timeout" : {
            if(check_obj(timeout_obj.timeout_list) > 1){
                var update_obj = JSON.parse(JSON.stringify(timeout_obj)) ;

                for(var list in timeout_obj.timeout_list){
                    if(list !== getAttributeByIndex(timeout_obj.timeout_list, 0)){
                        update_obj.timeout_list[list].delivery_user_ids = get_obj_from_array(timeout_obj.timeout_list[list].delivery_user_ids, users);
                        update_obj.timeout_list[list].message = timeout_obj.timeout_list[list].message_id ? get_template(timeout_obj.timeout_list[list].message_id, templates) : null;
                        update_obj.timeout_list[list].from = timeout_obj.timeout_list[list].from_id ? get_template(timeout_obj.timeout_list[list].from_id, templates) : null;
                    }
                }
                delete update_obj.timeout_list[getAttributeByIndex(timeout_obj.timeout_list, 0)];

                return update_obj ;   
            }else{
                return {} ;
            }
            break;
        }

        case "user_calculated" : {

            if(timeout_obj.timeout_list){
                for(var key in timeout_obj.timeout_list){
                    if(key == getAttributeByIndex(timeout_obj.timeout_list, 0)){
                        timeout_obj.timeout_list[key].delivery_user_ids = get_obj_from_array(timeout_obj.timeout_list[key].delivery_user_ids, users);
                        timeout_obj.timeout_list[key].template = {
                            message : timeout_obj.timeout_list[key].message_id ? get_template(timeout_obj.timeout_list[key].message_id, templates) : null ,
                            from    : timeout_obj.timeout_list[key].from_id ? get_template(timeout_obj.timeout_list[key].from_id, templates) : null
                        };
                    }
                }

                return timeout_obj.timeout_list[getAttributeByIndex(timeout_obj.timeout_list, 0)] ; 
            }else{
                return {} ; 
            }
            break;
        }

        default: {
            return {};
        }
    }
}

function calculate_page_id(task_obj, details_arr){
    var page_id = false ;
    if(task_obj !== null && details_arr !== undefined){
        var task_id = task_obj._id ;
        if(details_arr.length){
            for(var i=0; i < details.length; i++){
                if(details[i].task_id === task_id){
                  page_id = details[i].page_id ;
                }
            }
            if(!page_id){
              return generate_page_id(6);
            }else{
              return page_id;
            }
        }else{
            page_id = task_obj.page_id ;
        }
    }else{
        return generate_page_id(6);
    }
}

