function get_obj_from_array(id, arr){

    if(typeof id === "object"){
        return id ;    
    }

    var obj = {} ;
    if(arr !== null && arr.length){
        for(var i=0; i< arr.length; i++){
            if(arr[i]._id === id){
                obj[arr[i]._id] = arr[i] ;
            }
        }
        return obj ;
    }else{
        return obj ;
    }
}

function getAttributeByIndex(obj, index){
  var i = 0;
  for (var attr in obj){
    if (index === i){
      return parseInt(attr);
    }
    i++;
  }
  return null;
}

