}else{
    if(tasks_arr.length){
        var tasks = [];
        for(var i=0; i < tasks_arr.length; i++){
            tasks.push({
                id           : tasks_arr[i]._id,
                task_name    : tasks_arr[i].task_name || "N/A"     
            });
        }
        obj.status = 200 ;
        obj.error  = false ;
        obj.msg    = "Record fecthed successfully";
        obj.data   = tasks ;
        msg.payload = obj ;
    }else{
        obj.status = 200 ;
        obj.error  = false ;
        obj.msg    = "There is not task present";
        msg.paylod = obj ;
    }
}

return msg;

