function populate_record(list){
    var timeout = [], users = [], groups = [], details = [], task_table = [], sorted_detail = [];
    for(var itr in list){ 

        if(list[itr].value.table == "details"){
            details.push(list[itr].value);
        }

        if(list[itr].value.table == "task_table"){
            task_table.push(list[itr].value);
        }

        if(list[itr].value.table == "timeout"){
            timeout.push(list[itr].value);
        }

        if(list[itr].value.table == "users"){
            users.push(list[itr].value);
        }

        if(list[itr].value.table == "group_table"){
            groups.push(list[itr].value);
        }
    }

    if(!isEmpty(details)){
        sorted_detail =  details.sort(function(a,b){
            sorted_detail = sorted_detail.sort(function(a,b){
        		return (a.createdAt - b.createdAt);	
            });
        });
    }else{
        sorted_detail = details ;
    }

    return {groups: groups, timeout: timeout, users: users, details:sorted_detail, task_table:task_table};
}

function if_exists_in_arr(key, array){
