function populate_stumped_task(task_tables, task_name){
    var task_table = {};
    if(isEmpty(task_tables)) return task_table;
    if(isEmpty(task_name)) return task_name ;

    for(var itr in task_tables){
        if(task_tables[itr].task_name == task_name){
            task_table = task_tables[itr];
        }
    }

    return task_table;
}

function populate_records(list){
    var timeout = [], users = [], groups = [], details = [], task_table = [];
    for(var itr in list){ 

        if(list[itr].value.table == "details"){
            details.push(list[itr].value);
        }

        if(list[itr].value.table == "task_table"){
            task_table.push(list[itr].value);
        }

        if(list[itr].value.table == "timeout"){
            timeout.push(list[itr].value);
        }

        if(list[itr].value.table == "users"){
            users.push(list[itr].value);
        }

        if(list[itr].value.table == "group_table"){
            groups.push(list[itr].value);
        }
    }
    return {groups: groups, timeout: timeout, users: users, details:details, task_table:task_table};
}

function populate_obj(list, id){
    var obj = {};
    if(isEmpty(list)) return obj;
    if(isEmpty(id)) return obj;
    for(var itr in list){
        if(list[itr]._id == id){
            obj = list[itr];
        }
    }

    return obj;
}

