function populate_device_ids(users){
    var device_ids = [];
    if(isEmpty(users))  return device_ids;

    for(var itr in users){
        if(
            (users[itr].device_id !== "Web View") && !(isEmpty(users[itr].device_id)) 
        ){
            device_ids.push(users[itr].device_id);
        }
    }
    return device_ids;
}

function populate_phone_numbers(users){
    var numbers = [];
    if(isEmpty(users))  return numbers;

    for(var itr in users){
        if( !(isEmpty(users[itr].phone)) && (users[itr].phone.toString().length > 5) ){
            numbers.push(Number(users[itr].phone));
        }
    }
    return numbers;
}

function populate_email_address(users){
