function populate_group(list){
    var push_notification = {}, sms_notification = {}, email_notification = {}, app_notification ={};
    if(isEmpty(list)) return obj;

    for(var itr in list){
        if(!("company_bot_detail" in list[itr].type)){
            if(!isEmpty(list[itr].user_calculate)){
                var user_calculate = list[itr].user_calculate;
                if(user_calculate.delivery_via == "sms"){
                    sms_notification = {
                        users: user_calculate.delivery_user_special ,
                        msg  : list[itr].user_incoming.message
                    };
                }

                if(user_calculate.delivery_via == "push"){
                    push_notification = {
                        users: user_calculate.delivery_user_special,
                        msg  : list[itr].user_incoming.message
                    };
