function getContext(user){
    if(isEmpty(user.data)){
        return {
            first_name : user.firstname.toString(),
            last_name  : user.lastname.toString(),
            phone     : user.virtual_phone.toString(),
            timeStamp : new Date().getTime()
        };
    }else{
        if(isEmpty(user.data["fa1785333117ba7c06b9020a6d3c0079"])){
            return {
                first_name : user.firstname.toString(),
                last_name  : user.lastname.toString(),
                phone     : user.virtual_phone.toString(),
                timeStamp : new Date().getTime()
            };
        }else{
            var context = user.data["fa1785333117ba7c06b9020a6d3c0079"].context;
            if(!isEmpty(context)){
                if(new Date().getTime() > ( Number(context.timeStamp) +  10800000 )){
                    return {
                        first_name : user.firstname.toString(),
                        last_name  : user.lastname.toString(),
                        phone     : user.virtual_phone.toString(),
                        timeStamp : new Date().getTime()
                    };
                }else{
                    context.first_name = user.firstname.toString() || context.first_name ;
                    context.last_name  = user.lastname.toString() || context.last_name ;
                    context.phone     = user.virtual_phone.toString() || context.phone ;
                    context.timeStamp = new Date().getTime();
                    return context ;
                }
            }else{
                return {
                    first_name : user.firstname.toString(),
                    last_name  : user.lastname.toString(),
                    phone     : user.virtual_phone.toString(),
                    timeStamp : new Date().getTime()
                };
            }
        }
    }
}


function populate_task(list, task_name){
    var obj = {};
    for(var i =0; i<list.length; i++){
        if(task_name == list[i].task_name){
            obj = list[i];
        }
    }
    return obj;
}


function populate_record(list){
    var details=[], task_table = [], sorted_detail = [];

    for(var i=0; i<list.length; i++){
        if(list[i].value.table == "details"){
            details.push(list[i].value);
        }

        if(list[i].value.table == "task_table"){
            task_table.push(list[i].value);
        }
    }

    if(!isEmpty(details)){
        sorted_detail =  details.sort(function(a,b){
            sorted_detail = sorted_detail.sort(function(a,b){
        		return (a.createdAt - b.createdAt);	
            });
        });
    }else{
        sorted_detail = details ;
    }

    return {details: sorted_detail, tasks: task_table} ;
}

