function populate_device_token(device_detail, req_body){
    if(isEmpty(device_detail)){
        if( (req_body.device_id == "Web View") || (req_body.device_type == "Web Browser")){
            return {};
        }else{
            var temp = {};
            temp[req_body.device_fingerprint] = {
              device_type: req_body.device_type ,
              device_id  : req_body.device_id
            };
            return temp ;
        }
    }else{
        var local_temp = {};
        local_temp[req_body.device_fingerprint] = {
          device_type: req_body.device_type ,
          device_id  : req_body.device_id
        };
        return Object.assign(device_detail, local_temp);
    }    
}

