function compare_dates(due_date){
    var current_unix_date   = new Date().getTime(),
        due_unix_date       = new Date(due_date).getTime();

    if(due_unix_date > current_unix_date){
        return true ;
    }else{
        return false ;
    }
}

function create_detail_by_timeout_processor(){
    var detail = JSON.parse(JSON.stringify(detail_obj));  

    if(detail.timeout !== null){
        if(check_obj(detail.timeout.timeout_list) > 0){
            var clone_obj = {
                table                       : "details",
                task_id                     : detail_obj.task_id ,
                child_task_id               : detail_obj.child_task_id,
                user_id                     : detail_obj.user_id,
                page_id                     : detail_obj.page_id,
                synchronized                : detail_obj.synchronized,
                processed                   : detail_obj.processed,
                status                      : detail_obj.status,
                read                        : detail_obj.read,
                display_if_empty            : detail_obj.display_if_empty,
                date_created                : detail_obj.date_created,
                due_date                    : detail_obj.due_date,
                offline_expiration_seconds  : detail_obj.offline_expiration_seconds,
                priority                    : detail_obj.priority,
                user_incoming               : detail_obj.user_incoming ,
                watson_incoming             : detail_obj.watson_incoming,
                template                    : detail_obj.template,
                timeout                     : create_timeout_list_obj(detail.timeout, template_arr, users_arr, "timeout") ,
                user_calculated             : create_timeout_list_obj(detail_obj.timeout, template_arr, users_arr, "user_calculated"),
                location                    : detail_obj.location,
                default                     : detail_obj.default,
                createdAt                   : detail_obj.createdAt,
                count                       : detail_obj.count
            };

            return clone_obj ;
        }else{
            return {} ;
        }   
    }else{
        return {};
    }
}

function create_timeout_list_obj(timeout_obj, templates, users, type){
    switch(type){
        case "timeout" : {
            if(check_obj(timeout_obj.timeout_list) > 1){

                for(var list in timeout_obj.timeout_list){
                    if(parseInt(list) !== getAttributeByIndex(timeout_obj.timeout_list, 0)){
                        timeout_obj.timeout_list[list].delivery_user_ids = get_obj_from_array(timeout_obj.timeout_list[list].delivery_user_ids, users);
                        timeout_obj.timeout_list[list].message = timeout_obj.timeout_list[list].message_id ? get_template(timeout_obj.timeout_list[list].message_id, templates) : null;
                        timeout_obj.timeout_list[list].from = timeout_obj.timeout_list[list].from_id ? get_template(timeout_obj.timeout_list[list].from_id, templates) : null;
                    }
                }

                delete timeout_obj.timeout_list[getAttributeByIndex(timeout_obj.timeout_list, 0)];
                return timeout_obj ;   
            }else{
                return {} ;
            }
            break;
        }

        case "user_calculated" : {
            if(timeout_obj.timeout_list){
                for(var key in timeout_obj.timeout_list){
                    if(parseInt(key) == getAttributeByIndex(timeout_obj.timeout_list, 0)){
                        timeout_obj.timeout_list[key].delivery_user_ids = get_obj_from_array(timeout_obj.timeout_list[key].delivery_user_ids, users);
                        timeout_obj.timeout_list[key].template = {
                            message : timeout_obj.timeout_list[key].message_id ? get_template(timeout_obj.timeout_list[key].message_id, templates) : null ,
                            from    : timeout_obj.timeout_list[key].from_id ? get_template(timeout_obj.timeout_list[key].from_id, templates) : null
                        };
                    }
                }

                return timeout_obj.timeout_list[getAttributeByIndex(timeout_obj.timeout_list, 0)] ; 
            }else{
                return {} ; 
            }
            break;
        }

        default: {
            return {};
        }
    }
}

function check_obj(obj){
    return Object.keys(obj).length ;
}

function get_template(id, template){
    var obj = {} ;
    if(template !== null){
        for(var i=0; i< template.length; i++){
            if(id === template[i]._id){
                obj = template[i] ;
            }
        }    
        return obj ;
    }else{
        return obj ;
    }
}

function get_obj_from_array(id, arr){

    if(id instanceof Object){
        return id ;    
    }

    var obj = {} ;
    if(arr !== null && arr.length){
        for(var i=0; i< arr.length; i++){
            if(arr[i]._id === id){
                obj[arr[i]._id] = arr[i] ;
            }
        }
        return obj ;
    }else{
        return obj ;
    }
}

function getAttributeByIndex(obj, index){
  var i = 0;
  for (var attr in obj){
    if (index === i){
      return parseInt(attr);
    }
    i++;
  }
  return null;
}

