function update_user_task(user_task, user_id, task_id, status, synchronized){

    user_task.synchronized          = synchronized  ;
    user_task.date_updated          = new Date().toJSON();
    user_task.templates             = detail_obj.template ;
    user_task.timeout               = detail_obj.timeout ;
    user_task.count_status.unread   = detail_obj.read ;
    user_task.page_id               = parseInt(detail_obj.to_page_id) ;
    user_task.status                = detail_obj.status || status;

    if(!user_task.update_read){
        if( ( user_task.count_status.unread === 1 || user_task.count_status.unread === "1") && !user_task.count_if.unread ){
            user_task.count.unread += 1 ;  
        }
    }

    if(!user_task.update_active){
        if( user_task.count_status.active && user_task.count_if.active){
            user_task.count.active += 1 ;  
        }
    }

    //if(user_task.parent_id) update_user_task(user_task, user_id, user_task.parent_id, 0, 1);

    user_task.update_read   = 1 ;
    user_task.update_active = 1 ;

    return user_task;
}

