function populate_db_record(rows){
    var template = [], task_table = [], timeout=[], location=[], users=[], task_obj={}; 
    for(var i=0; i<rows.length; i++){
        if(rows[i].doc.table == "template"){
            template.push(rows[i].doc);
        }

        if(rows[i].doc.table == "task_table"){
            if(rows[i].doc.page_id == task_id){
                task_obj = rows[i].doc ;
            }else{
                task_table.push({
                    id          : rows[i].doc._id,
                    task_name   : rows[i].doc.task_name
                });
            }
        }

        if(rows[i].doc.table == "timeout"){
            timeout.push({
                id     : rows[i].doc._id,
                name   : rows[i].doc.name
            });
        }

        if(rows[i].doc.table == "location"){
            location.push({
                id          : rows[i].doc._id,
                name        : rows[i].doc.location_name
            });
        }

        if(rows[i].doc.table == "users"){
            users.push({
                id          : rows[i].doc._id,
                name        : formulate_name(rows[i].doc)
            });
        }
    }

    return {
      formatted_template: template,
      formatted_task    : task_table,
      formatted_timeout : timeout,
      formatted_location: location,
      formatted_user    : users,
      task_obj          : task_obj
    };
}

