function getContext(user, portal_user){
    if(isEmpty(user.data) || isEmpty(portal_user)){
        return {
            first_name : user.firstname.toString(),
            last_name  : user.lastname.toString(),
            phone     : user.virtual_phone.toString(),
            timeStamp : new Date().getTime()
        };
    }else{

        if(isEmpty(user.data[portal_user._id])){
            return {
                first_name : user.firstname.toString(),
                last_name  : user.lastname.toString(),
                phone     : user.virtual_phone.toString(),
                timeStamp : new Date().getTime()
            };
        }else{
            var context = user.data[portal_user._id].context;
            if(!isEmpty(context)){
