    }
}

function get_obj_data(obj1, obj2){
	var obj = {};
	for (var key in obj1) {
		if(obj2.hasOwnProperty(key))
			obj[key] = obj2[key];
		else
		    obj[key] = obj1[key];
	}
	return obj;
}

