            return false;
        }else{
            return true;
        }
    }else if(Object.prototype.toString.call(obj) === "[object Array]"){
        if( obj.length ){
            return false;
        }else{
            return true;
        }
    }else{
        if( obj ){
            return false;
        }else{
            return true;
        }
    }   
}

