$scope.verify = function(obj) {
	if (alertPopup) alertPopup.close();
	var verificationCode = String(obj.code1) + String(obj.code2) + String(obj.code3) + String(obj.code4) ;
	verificationCode = verificationCode.trim();

	if (verificationCode.length === 4) {
		utilityService.setBusy(true, 'loading...');
		var endpoint = $scope.api_url + "/api/verify";
		var config = {
			params: {
				code: verificationCode,
				phone: $localStorage.logged_user_phone,
				device_id: $localStorage.device_id,
				device_type: $localStorage.deviceType,
				device_fingerprint: $localStorage.deviceFingerPrint,
				push_accepted: $localStorage.push_accepted || 0
			}
		};
		console.log("verification config params>>>" + JSON.stringify(config));
		$http.get(endpoint, config).then(function(res) {
			var res_data = res.data;
			myService.apiResult = res_data;
			if (res_data.status == "valid") {
				utilityService.setBusy(false);
				$localStorage.user_id = res_data.user_id;
				$localStorage.access_token = res_data.access_token;
				$scope.verification = {code1: "", code2: "", code3: "", code4: ""};
				$scope.goPage(res_data.page_id);
			} else {
				utilityService.setBusy(false);
 				alertPopup = utilityService.showAlert(res_data.msg || "Invalid code.");
			}
		});
		return false;
	}
	return false;
};

