$scope.login = function(user) {
	var number = user.phone ;
	if (!number) {
		utilityService.showAlert("Please enter phone number").then(function(res) {
			$timeout(function() { $("#phone").focus(); }, 100);
		});
		return false;
	}

	number = number.replace(/[- )(]/g,"").trim();
	var reg = new RegExp("^(0|[1-9][0-9]*)$");

	if(!reg.test(number)){
		utilityService.showAlert("Please enter valid phone number").then(function(res) {
			$timeout(function() { $("#phone").focus(); }, 100);
		});
		return false;
	}
	number = user.country_code + number ;
	var endpoint = $scope.api_url + "/api/login";
	var parameters = {
		phone:	number,
		access_token:	$localStorage.access_token,
		device_id : $localStorage.device_id ,
		push_accepted: $localStorage.push_accepted
	};

	if(number == $localStorage.logged_user_phone){
		utilityService.setBusy(true);
		$http.post(endpoint, parameters).then(function(res) {
			var res_data = res.data;
			console.log("login res_data>>>", res_data);
			myService.apiResult = res_data;
			$localStorage.logged_user_phone = number;
			$scope.goToVerifyPage(res_data);
		});
	}else{
		utilityService.setBusy(true);
		$http.post(endpoint, parameters).then(function(res) {
			var res_data = res.data;
			console.log("login res_data>>>", res_data);
			myService.apiResult = res_data;
			$localStorage.logged_user_phone = number;
			$scope.goToVerifyPage(res_data);
		});
	}
};

$scope.checkNumber = function(){
	if ($scope.user !== undefined && $scope.user.phone !== "") {
		var input = $scope.user.phone.replace(/[^0-9\(\)\s\-]/g, "");
		var inputlen = input.length;
		// Get just the numbers in the input
		var numbers = $scope.user.phone.replace(/\D/g, '');
		var numberslen = numbers.length;
		// Value to store the masked input
		var newval = "";

		// Loop through the existing numbers and apply the mask
		for (var i = 0; i < numberslen; i++) {
			if (i == 0) newval = "(" + numbers[i];
			else if (i == 3) newval += ") " + numbers[i];
			else if (i == 6) newval += "-" + numbers[i];
			else newval += numbers[i];
		}

		// Re-add the non-digit characters to the end of the input that the user entered and that match the mask.
		if (inputlen >= 1 && numberslen == 0 && input[0] == "(") newval = "(";
		else if (inputlen >= 6 && numberslen == 3 && input[4] == ")" && input[5] == " ") newval += ") ";
		else if (inputlen >= 5 && numberslen == 3 && input[4] == ")") newval += ")";
		else if (inputlen >= 6 && numberslen == 3 && input[5] == " ") newval += " ";
		else if (inputlen >= 10 && numberslen == 6 && input[9] == "-") newval += "-";

		//$(this).val(newval.substring(0, 14));
		$scope.user.phone = newval.substring(0, 14);
	}
};

function seprateNumber(number){
	var obj = {};
	if(number !== null && number !== undefined){
		if(number.length > 4){
			obj.countryCode = number.substr(0, number.length - 10);
			number 			= number.substr(number.length - 10, number.length);
			obj.number 		= "(" + number.substr(0, 3) + ") " + number.substr(3, 3) + "-" + number.substr(6, 4);
		}
	}else{
		obj.countryCode = "1";
		obj.number = "";
	}
	return obj ;
}

